$(document).ready(function() {
    $.simpleWeather({
        location: "Vancouver, BC",
        woeid: "",
        unit: "c",
        success: function(i) {
            html = '<h2><i class="icon-' + i.code + '"></i> ' + i.temp + "&deg;" + i.units.temp + "</h2>", html += "<ul><li>" + i.city + ", " + i.region + "</li>", html += '<li class="currently">' + i.currently + "</li>", html += "<li>" + i.wind.direction + " " + i.wind.speed + " " + i.units.speed + "</li></ul>", $("#weather").html(html)
        },
        error: function(i) {
            $("#weather").html("<p>" + i + "</p>")
        }
    })
});
